mapper.insert:
INSERT
  INTO
  		CHAT
  		(
  		CHAT_NO
  		,USER_ID
  		,TITLE
  		,CONTENT
  		)
VALUES
		(
		SEQ_CNO.NEXTVAL
		,#{userId}
		,#{title}
		,#{content}
		)

INSERT
  INTO
  		CHAT_DETAIL
 		(
 		DETAIL_NO
 		,CHAT_NO
 		,USER_ID1
 		,USER_ID2
 		)
 VALUES
 		(
 		SEQ_DNO.NEXTVAL
 		,#{chatNo}
 		,#{userId}
 		,#{userId2}
 		)


  			SELECT
  					DETAIL_NO
  			  FROM
  			  		CHAT_DETAIL
  			 WHERE
  			 		CHAT_NO = #{chatNo}
  			   AND
  			   		USER_ID2 = #{userId2}

  		INSERT
		  INTO
		  		CHAT_CONTENT
		  		(
		  		DETAIL_NO
		  		,CHAT_NO
		  		,USER_ID
		  		,CONTENT
		  		)
		 VALUES
		 		(
		 		#{detailNo}
		 		,#{chatNo}
		 		,#{userId2}
		 		,#{content}
		 		)
